script:
frappe.ui.form.on('Sales Invoice', {

  //to fix Slip Nos disappear On Save.This helps to make it stay on the field. 
    after_save: function(frm) {
        if (frm.doc.custom_slip_no) {
            // Add the value to options if it's missing
            let field = frm.fields_dict['custom_slip_no'];
            if (!field.df.options.includes(frm.doc.custom_slip_no)) {
                field.df.options += `\n${frm.doc.custom_slip_no}`;
                field.refresh();
            }
            // Set the value
            frm.set_value('custom_slip_no', frm.doc.custom_slip_no);
            frm.refresh_field('custom_slip_no');
        }
    },

    refresh: function(frm) {
        // Attach click event to custom new slip button
        frm.fields_dict['custom_new_slip'].input.onclick = () => {
            open_slip_number_dialog(frm);
        };
                $(document).ready(function() {
            // Listen for changes or clicks on the label for 'Rounded Total (INR)'
            $('label.control-label:contains("Rounded Total (INR)")').each(function() {
                const label = $(this);
                const relatedField = label.closest('.form-group').find('input, .control-value'); // Find the related field

                // Using MutationObserver to detect changes to the field content
                const observer = new MutationObserver(function(mutations) {
                    mutations.forEach(function(mutation) {
                        if (mutation.type === 'childList' || mutation.type === 'characterData') {
                            console.log("Value changed for Rounded Total (INR) field!");
                            update_allocated_amount(frm);
                        }
                    });
                });

                // Observe for changes in the related field's content
                const config = { childList: true, characterData: true, subtree: true };
                if (relatedField.length > 0) {
                    observer.observe(relatedField[0], config);
                }
            });
        });


        // Load slip numbers on refresh
        load_slip_numbers(frm);
    },

    customer: function(frm) {
        // Load slip numbers when customer changes
        load_slip_numbers(frm);
        frm.set_value('custom_slip_no', ''); // Clear the field when customer changes
    },

    custom_slip_no: function(frm) {
        if (!frm.doc.custom_slip_no) return;

        // Clear existing advances
        frm.clear_table('advances');

        frappe.call({
            method: 'sil.services.payment_entry_api.get_payment_entry_by_slip',
            args: {
                slip_no: frm.doc.custom_slip_no
            },
            callback: function(r) {
                if (r.message && r.message.length) {
                    r.message.forEach(entry => {
                        let row = frm.add_child('advances');
                        row.reference_type = 'Payment Entry';
                        row.reference_name = entry.NAME;
                        row.advance_amount = entry.paid_amount;
                        row.remarks = entry.remarks;
                        row.custom_slip_no = entry.custom_slip_no;
                    });

                    frm.refresh_field('advances');
                    update_allocated_amount(frm);

                    frappe.show_alert({
                        message: __('Advanced payments loaded from slip number'),
                        indicator: 'green'
                    });
                } else {
                    frappe.show_alert({
                        message: __('No payment entries found for this slip number'),
                        indicator: 'orange'
                    });
                }
            }
        });
    }
});

// Function to update allocated amount for each row in 'advances'
function update_allocated_amount(frm) {
    if (!frm.doc.advances || !frm.doc.advances.length) return;

    let total_allocated = 0;

    frm.doc.advances.forEach(row => {
        if (frm.doc.rounded_total >= (total_allocated + row.advance_amount)) {
            row.allocated_amount = row.advance_amount;
        } else {
            let remaining = frm.doc.rounded_total - total_allocated;
            row.allocated_amount = remaining > 0 ? remaining : 0;
        }
        total_allocated += row.allocated_amount;
    });

    frm.refresh_field('advances');
    frm.trigger('form_render');
}

// Function to load slip numbers based on customer
function load_slip_numbers(frm) {
    frappe.call({
        method: 'sil.services.payment_entry_api.get_custom_slip_nos',
        args: {
            filters: frm.doc.customer ? { 'party': frm.doc.customer } : {}
        },
        callback: function(r) {
            if (r.message) {
                let field = frm.fields_dict['custom_slip_no'];
                let slip_numbers = r.message;

                // Add the current value to options if missing
                const current_value = frm.doc.custom_slip_no;
                if (current_value && !slip_numbers.includes(current_value)) {
                    slip_numbers.push(current_value);
                }

                // Set options as an array instead of string
                field.df.options = slip_numbers;
                field.refresh();
            }
        }
    });
}

// Dialog to enter slip number
function open_slip_number_dialog(frm) {
    const dialog = new frappe.ui.Dialog({
        title: 'Enter Slip Number',
        fields: [
            {
                label: 'Slip Number',
                fieldname: 'slip_number',
                fieldtype: 'Data',
                reqd: 1,
                onchange: function() {
                    // Reset field styling
                    const field = dialog.get_field('slip_number');
                    field.$input.css('border-color', '');
                    field.$input.css('background-color', '');
                }
            }
        ],
        primary_action_label: 'Submit',
        primary_action(values) {
            if (!values.slip_number) {
                frappe.msgprint({
                    title: 'Error',
                    message: 'Please enter a slip number',
                    indicator: 'red'
                });
                return;
            }

            // Check if slip number exists in Payment Entries
            frappe.call({
                method: 'frappe.client.get_list',
                args: {
                    doctype: 'Payment Entry',
                    filters: { custom_slip_no: values.slip_number },
                    fields: ['name']
                },
                callback: function(r) {
                    if (r.message && r.message.length > 0) {
                        const field = dialog.get_field('slip_number');
                        field.$input.css({ 'border-color': 'red', 'background-color': '#fff0f0' });
                        frappe.msgprint({
                            title: 'Error',
                            message: 'This slip number already exists in Payment Entry.',
                            indicator: 'red'
                        });
                    } else {
                        // Check if slip number exists in Sales Invoice
                        frappe.call({
                            method: 'frappe.client.get_list',
                            args: {
                                doctype: 'Sales Invoice',
                                filters: {
                                    custom_slip_no: values.slip_number,
                                    docstatus: 1
                                },
                                fields: ['name']
                            },
                            callback: function(r2) {
                                if (r2.message && r2.message.length > 0) {
                                    const field = dialog.get_field('slip_number');
                                    field.$input.css({ 'border-color': 'red', 'background-color': '#fff0f0' });
                                    frappe.msgprint({
                                        title: 'Error',
                                        message: 'This slip number already exists in Sales Invoice.',
                                        indicator: 'red'
                                    });
                                } else {
                                    // Check if slip number exists in Journal Entry Account
                                    frappe.call({
                                        method: 'sil.services.payment_entry_api.check_slip_duplicate',
                                        args: {
                                            slip_no: values.slip_number
                                            },
                                        callback: function(r3) {
                                            if (r3.message && r3.message.length > 0) {
                                                const field = dialog.get_field('slip_number');
                                                field.$input.css({ 'border-color': 'red', 'background-color': '#fff0f0' });
                                                frappe.msgprint({
                                                    title: 'Error',
                                                    message: 'This slip number already exists in Journal Entry Account.',
                                                    indicator: 'red'
                                                });
                                            } else {
                                                // Add slip number to options and set value
                                                if (!frm.fields_dict.custom_slip_no.df.options.includes(values.slip_number)) {
                                                    frm.fields_dict.custom_slip_no.df.options = [values.slip_number];
                                                    frm.fields_dict.custom_slip_no.refresh();
                                                }
                                                frm.set_value('custom_slip_no', values.slip_number);
                                                dialog.hide();

                                                frappe.show_alert({
                                                    message: `Slip number ${values.slip_number} has been set successfully.`,
                                                    indicator: 'green'
                                                }, 3);
                                            }
                                        }
                                    });
                                }
                            }
                        });
                    }
                }
            });
        }
    });

    dialog.show();
}

frappe.listview_settings['Attendance'] = {
    onload: function(listview) {
        // Add a custom button to the Attendance list view toolbar
        listview.page.add_inner_button(__('Export Check-in Report'), function() {
            open_filter_dialog();
        });
    }
};

function open_filter_dialog() {
    // Create a dialog with employee, from date, and to date fields
    let dialog = new frappe.ui.Dialog({
        title: 'Filter for Check-in Report',
        fields: [
            {
                label: 'All Employees',
                fieldname: 'all_employees',
                fieldtype: 'Check',
                default: 0,  // Default to unchecked
                reqd: 0  // Optional
            },
            {
                label: 'Employee',
                fieldname: 'employee',
                fieldtype: 'Link',
                options: 'Employee',
                reqd: 0,  // Optional
                depends_on: 'eval:!doc.all_employees',
                mandatory_depends_on: 'eval:!doc.all_employees'
            },
            {
                fieldtype: 'Column Break'
            },
            {
                label: 'From Date',
                fieldname: 'from_date',
                fieldtype: 'Date',
                default: frappe.datetime.get_today(),
                columns: 2,
                reqd: 1  // Required field
            },
            {
                label: 'To Date',
                fieldname: 'to_date',
                fieldtype: 'Date',
                default: frappe.datetime.get_today(),
                columns: 2,
                reqd: 1  // Required field
            }
        ],
        primary_action_label: 'Generate Report',
        primary_action(values) {
            // Ensure valid input
            if (!values.from_date || !values.to_date) {
                frappe.msgprint(__('Please fill all required fields.'));
                return;
            }

            if (values.to_date < values.from_date) {
                frappe.msgprint(__('To Date cannot be earlier than From Date.'));
                return;
            }

            if (values.all_employees) {
                values.employee = '';  // Clear employee if all employees are selected
            }

            // Show a progress dialog
            let progress_dialog = frappe.msgprint({
                message: __('Generating report, please wait...'),
                indicator: 'blue',
                title: __('Processing')
            });

            // Call the server-side function to generate the report
            frappe.call({
                method: 'sil.services.checkin_report_api.get_combined_checkin_report_to_hr',
                args: {
                    employee_name: values.employee || '',
                    from_date: values.from_date,
                    to_date: values.to_date
                },
                callback: function(response) {
                    // Hide the progress dialog
                    progress_dialog.hide();

                    if (response.message && response.message.file_url) {
                        // Hide the progress dialog in case of an error
                        progress_dialog.hide();

                        const fileUrl = response.message.file_url;
                        const link = document.createElement('a');
                        link.href = fileUrl;
                        link.download = response.message.file_name || 'Checkin_Report.xlsx';
                        link.click();

                        frappe.msgprint({
                            title: __('Success'),
                            indicator: 'green',
                            message: __('Check-in report successfully downloaded.')
                        });
                        progress_dialog.hide();
                    } else {
                        // Hide the progress dialog in case of an error
                        progress_dialog.hide();
                        frappe.msgprint({
                            title: __('Error'),
                            indicator: 'red',
                            message: __('Error: {0}', [response.message.error || 'Unknown error'])
                        });
                        progress_dialog.hide();
                    }
                },
                error: function(error) {
                    // Hide the progress dialog in case of an error
                    progress_dialog.hide();

                    frappe.msgprint({
                        title: __('Error'),
                        indicator: 'red',
                        message: __('Server error occurred while generating the report.')
                    });
                    console.error('Server error:', error);
                }
            });

            dialog.hide();  // Hide the filter dialog
        }
    });

    dialog.show();  // Show the filter dialog
}

frappe.listview_settings['Sales Invoice'] = {
    onload: function(listview) {
        listview.page.add_inner_button(__('Export Report'), function() {
            open_download_dialog();
        });
    }
};

function open_download_dialog() {
    const dialog = new frappe.ui.Dialog({
        title: __('Download Sales Invoice Report'),
        width: '800px',
        height: '300px',
        fields: [
            {
                fieldtype: 'Section Break',
                label: __('Date Filters'),
            },
            {
                fieldtype: 'Column Break'
            },
            {
                fieldtype: 'Date',
                fieldname: 'starting_posting_date',
                label: __('From Date'),
                reqd: 1,
                default: frappe.datetime.get_today(),
                columns: 3,
                description: __('Select the start date for the report.')
            },
            {
                fieldtype: 'Date',
                fieldname: 'ending_posting_date',
                label: __('To Date'),
                reqd: 1,
                default: frappe.datetime.get_today(),
                columns: 3,
                description: __('Select the end date for the report.')
            },
            {
                fieldtype: 'Column Break'
            },
            {
                fieldtype: 'Link',
                fieldname: 'customer_name',
                label: __('Customer Name'),
                options: 'Customer',
                reqd: 0,
                columns: 3,
                description: __('Select the Customer for the report.')
            },
            {
                fieldtype: 'Section Break',
                label: __('Manager Filters'),
            },
            {
                fieldtype: 'Select',
                fieldname: 'custom_zonal_manager',
                label: __('Zonal Manager'),
                options: [],
                reqd: 0,
                columns: 1,
                description: __('(Select the Zonal Manager for the report.)')
            },
            {
                fieldtype: 'Select',
                fieldname: 'custom_regional_manager',
                label: __('Regional Manager'),
                options: [],
                reqd: 0,
                columns: 1,
                description: __('(Select the Regional Manager for the report.)')
            },
            {
                fieldtype: 'Column Break'
            },
            {
                fieldtype: 'Select',
                fieldname: 'custom_cluster',
                label: __('Cluster'),
                options: [],
                reqd: 0,
                columns: 1,
                description: __('(Select the Cluster for the report.)')
            },
            {
                fieldtype: 'Select',
                fieldname: 'custom_cluster_manager',
                label: __('Cluster Manager'),
                options: [],
                reqd: 0,
                columns: 1,
                description: __('Select the Cluster Manager for the report.')
            }
        ],
        primary_action_label: __('Download'),
        primary_action(values) {
            if (values.starting_posting_date > values.ending_posting_date) {
                frappe.msgprint(__('Error: From Date cannot be later than To Date.'));
                return;
            }

            frappe.call({
                method: 'sil.services.sales_invoice_report_api.generate_and_download_sales_invoice_report',
                args: {
                    filters: values
                },
                callback: function(response) {
                    if (response.message && response.message.file_url) {
                        const link = document.createElement('a');
                        link.href = response.message.file_url;
                        link.download = response.message.file_name || 'sales_invoice_report.xlsx';
                        link.click();
                    } else {
                        frappe.msgprint(__('Error: {0}', [response.message.error || 'Unknown error']));
                    }
                },
                error: function(err) {
                    frappe.msgprint(__('An unexpected error occurred. Please try again.'));
                    console.error(err);
                }
            });

            dialog.hide();
        }
    });

    // Fetch filter data from the server
    frappe.call({
        method: 'sil.services.sales_invoice_api.get_distinct_sales_invoice_filters',
        callback: function(response) {
            const data = response.message || [];

            // Populate options dynamically
            dialog.set_df_property('custom_zonal_manager', 'options', [''].concat([...new Set(data.map(item => item.custom_zonal_manager))]));
            dialog.set_df_property('custom_regional_manager', 'options', [''].concat([...new Set(data.map(item => item.custom_regional_manager))]));
            dialog.set_df_property('custom_cluster', 'options', [''].concat([...new Set(data.map(item => item.custom_cluster))]));
            dialog.set_df_property('custom_cluster_manager', 'options', [''].concat([...new Set(data.map(item => item.custom_cluster_manager))]));

            dialog.refresh();
        }
    });

    dialog.show();
}

frappe.ui.form.on('Sales Order', {
    custom_sales_type : function(frm) {
        frm.fields_dict.items.grid.get_field('item_code').get_query = function(doc, cdt, cdn) {
            let row = locals[cdt][cdn];
