        let row = locals[cdt][cdn];
        row.is_manually_set = true;

        // Only validate against outstanding for Sales Invoice and Sales Order
        if (row.reference_type === 'Sales Invoice' || row.reference_type === 'Sales Order') {
            let allocated = parseFloat(row.allocated_amount || 0.0);
            let outstanding = parseFloat(row.outstanding_amount || 0.0);

            if (allocated > outstanding) {
                frappe.msgprint(__('The allocated amount cannot exceed the outstanding amount.'));
                frappe.model.set_value(cdt, cdn, 'allocated_amount', outstanding);
                return;
            }
        }

        // Calculate total allocated amount excluding current row
        let total_allocated = 0;
        frm.doc.receipt_entry.forEach(function(r) {
            if (r.name !== row.name) {
                total_allocated += parseFloat(r.allocated_amount || 0.0);
            }
        });

        let max_possible = parseFloat(frm.doc.amount || 0.0) - total_allocated;
        let current_allocated = parseFloat(row.allocated_amount || 0.0);

        if (current_allocated > max_possible) {
            frappe.msgprint(__('The allocated amount cannot exceed the available unallocated amount.'));
            frappe.model.set_value(cdt, cdn, 'allocated_amount', max_possible);
            return;
        }

        update_parent_field(frm, cdt, cdn);
        update_unallocated_amount(frm);
    }
});

// Function to check for duplicate entries in the child table
function check_for_duplicate_entries(frm) {
    let seen_entries = new Set();
    frm.doc.receipt_entry.forEach(function(row) {
        let key = `${row.customer || ''}-${row.reference_type || ''}-${row.reference_name || ''}`;
        if (seen_entries.has(key)) {
            frappe.msgprint({
                title: __('Duplicate Entry'),
                message: __('Duplicate entry found for Customer: {0}, Reference Type: {1}, Reference Name: {2}', 
                    [row.customer, row.reference_type, row.reference_name]),
                indicator: 'red'
            });
            frappe.validated = false;
            frappe.throw(__('Please remove duplicate entries in the child table.'));
        }
        seen_entries.add(key);
    });
}

function check_for_slip_no_entries(frm) {
    frm.doc.receipt_entry.forEach(function(row) {
        if (row.reference_type === 'Slip No') {
            if (!row.customer || !row.reference_name || !row.allocated_amount || row.allocated_amount <= 0) {
                frappe.msgprint({
                    title: __('Missing Fields for Slip No Entry '),
                    message: __('Please fill the mandatory fields for Slip No entries.'),
                    indicator: 'red'
                });
                frappe.validated = false; // Prevent save action if validation fails
            }
        }
    });
}

// Function to fetch receipt information and handle validations
function check_and_call_server(frm, cdt, cdn) {
    let row = locals[cdt][cdn];
    if (!frm.doc.amount) {
        frappe.msgprint(__('Please enter the total amount.'));
        return;
    }

    row.is_manually_set = false;
    update_unallocated_amount(frm);

    if (!row.customer || !row.reference_type) return;

    if (!row.reference_name || row.reference_name === "") {
        refresh_reference_names(frm, cdt, cdn);
    } else {
        frappe.call({
            method: 'sil.services.receipt_information_api.get_all_receipt_info_by_reference_name',
            args: {
                customer: row.customer,
                reference_type: row.reference_type,
                reference_name: row.reference_name
            },
            callback: function(r) {
                if (r.message) {
                    let outstanding = parseFloat(r.message.outstanding_amount || 0.0);

                    // Set outstanding amount first
                    frappe.model.set_value(cdt, cdn, 'outstanding_amount', outstanding);

                    // Calculate available unallocated amount
                    let total_allocated = 0;
                    frm.doc.receipt_entry.forEach(function(r) {
                        if (r.name !== cdn) {
                            total_allocated += parseFloat(r.allocated_amount || 0.0);
                        }
                    });

                    let available_amount = parseFloat(frm.doc.amount || 0.0) - total_allocated;
                    let allocated = Math.min(outstanding, available_amount);

                    // Set allocated amount after ensuring it doesn't exceed outstanding
                    frappe.model.set_value(cdt, cdn, 'allocated_amount', allocated);

                    frm.fields_dict["receipt_entry"].grid.grid_rows_by_docname[cdn].refresh_field('reference_name');
                    frm.refresh_field('receipt_entry');
                    update_unallocated_amount(frm);
                }
            }
        });
    }
}

// Function to update parent fields based on child table entries
function update_parent_field(frm, cdt, cdn) {
    let total_allocated_amount = 0.0;
    frm.doc.receipt_entry.forEach(function(row) {
        if (row.reference_type === 'Sales Invoice' || row.reference_type === 'Sales Order') {
            if (parseFloat(row.allocated_amount || 0.0) > parseFloat(row.outstanding_amount || 0.0)) {
                frappe.msgprint(__('The allocated amount ({0}) cannot exceed the outstanding amount ({1}).',
                    [parseFloat(row.allocated_amount), parseFloat(row.outstanding_amount)]));
                frappe.model.set_value(cdt, cdn, 'allocated_amount', row.outstanding_amount);
                frappe.validated = false;
                return false;
            }
        }
        total_allocated_amount += parseFloat(row.allocated_amount || 0.0);
    });
    update_unallocated_amount(frm);
}

// Function to update unallocated amount in the parent form
function update_unallocated_amount(frm) {
    let total_amount = parseFloat(frm.doc.amount || 0.0);
    let unallocated = total_amount;

    frm.doc.receipt_entry.forEach(function(row) {
        let allocated_amount = parseFloat(row.allocated_amount || 0.0);
        let outstanding_amount = parseFloat(row.outstanding_amount || 0.0);

        if (row.reference_type === 'Sales Invoice' || row.reference_type === 'Sales Order') {
            // Ensure allocated amount never exceeds outstanding amount
            if (allocated_amount > outstanding_amount) {
                allocated_amount = outstanding_amount;
                frappe.model.set_value(row.doctype, row.name, 'allocated_amount', allocated_amount);
            }
        }

        unallocated -= allocated_amount;
    });

    // Round to 2 decimal places to avoid floating point issues
    unallocated = Number(unallocated.toFixed(2));

    if (Math.abs(unallocated) < 0.01) {
        frm.set_value('unallocated_amount', '0');
    } else {
        frm.set_value('unallocated_amount', unallocated);
    }

    if (unallocated < 0) {
        frappe.msgprint(__('The total allocated amount exceeds the total available amount.'));
        frappe.validated = false;
    }
}

function refresh_reference_names(frm, cdt, cdn) {
    let current_row = locals[cdt][cdn];

    // Get all currently selected reference names except for current row
    let selected_references = frm.doc.receipt_entry
        .filter(row => row.name !== cdn && row.reference_name)
        .map(row => row.reference_name);

    frappe.call({
        method: 'sil.services.receipt_information_api.get_all_receipt_info_by_reference_type_and_cust_name',
        args: {
            customer: current_row.customer,
            reference_type: current_row.reference_type
        },
        callback: function(r) {
            if (r.message && Array.isArray(r.message.reference_name)) {
                // Filter out already selected reference names
                let available_references = r.message.reference_name
                    .filter(item => !selected_references.includes(item.name))
                    .map(item => item.name);

                let refNameField = frm.fields_dict["receipt_entry"].grid.grid_rows_by_docname[cdn]
                    .get_field('reference_name');
                refNameField.df.options = available_references;
                refNameField.refresh();
            }
        }
    });
}

// Improved Awesomplete implementation
function setupAwesompleteForTable(frm) {
    // Apply Awesomplete to existing rows
    if(frm.doc.receipt_entry && frm.doc.receipt_entry.length > 0) {
        frm.doc.receipt_entry.forEach(function(row) {
            if(row.reference_type === "Sales Invoice" || row.reference_type === "Sales Order") {
                setupAwesompleteForRow(frm, row.doctype, row.name);
            }
        });
    }

    // Setup a hook for when the grid refreshes
    frm.fields_dict["receipt_entry"].grid.wrapper.on('refresh', function() {
        setTimeout(() => {
            frm.doc.receipt_entry.forEach(function(row) {
                if(row.reference_type === "Sales Invoice" || row.reference_type === "Sales Order") {
                    setupAwesompleteForRow(frm, row.doctype, row.name);
                }
            });
        }, 300);
    });
}

function setupAwesompleteForRow(frm, cdt, cdn) {
    const row = locals[cdt][cdn];
    const grid_row = frm.fields_dict.receipt_entry.grid.grid_rows_by_docname[cdn];

    if(!grid_row) return;

    // Wait for the DOM to be ready
    setTimeout(() => {
        // Find the reference_name cell
        const $cell = $(grid_row.row).find('[data-fieldname="reference_name"]');
        const $select = $cell.find('select');

        if($select.length === 0) return;

        // Check if we already applied Awesomplete to avoid duplicates
        if($cell.find('input.awesomplete-input').length > 0) return;

        // Create our custom search input
        const $search_input = $(`<input type="text" class="form-control awesomplete-input" placeholder="Search...">`);

        // Insert the search input before the select and hide the select
        $select.before($search_input);
        $select.css('display', 'none');

        // Initialize Awesomplete with contains filter
        const awesomplete = new Awesomplete($search_input[0], {
            minChars: 0, // Show all options even before typing
            autoFirst: true,
            maxItems: 15,
            filter: function(text, input) {
                // If no input, show all options
                if (input.length === 0) return true;
                // Otherwise use contains filter
                return Awesomplete.FILTER_CONTAINS(text, input);
            }
        });

        // Set initial value if one exists
        if(row.reference_name) {
            $search_input.val(row.reference_name);
        }

        // Get all currently selected reference names except for current row
        let selected_references = frm.doc.receipt_entry
            .filter(r => r.name !== cdn && r.reference_name)
            .map(r => r.reference_name);

        // Load initial options when the field gets focus
        $search_input.on('focus', function() {
            loadOptionsForRow(frm, row, awesomplete, '', selected_references);
            // Show dropdown immediately on focus
            setTimeout(() => {
                awesomplete.evaluate();
            }, 100);
        });

        // Handle search input changes
        $search_input.on('input', function() {
            const search_text = $(this).val();
            loadOptionsForRow(frm, row, awesomplete, search_text, selected_references);
        });

        // Handle selection from dropdown
        $search_input.on('awesomplete-selectcomplete', function(e) {
            const selected_value = e.originalEvent.text.value;
            $select.val(selected_value);

            // Update the model
            frappe.model.set_value(cdt, cdn, 'reference_name', selected_value);
            grid_row.refresh();
        });

        // Initial load of options
        loadOptionsForRow(frm, row, awesomplete, '', selected_references);
    }, 300);
}

// Load options for a specific row based on search text
function loadOptionsForRow(frm, row, awesomplete, search_text = '', selected_references = []) {
    frappe.call({
        method: 'sil.services.receipt_information_api.get_all_receipt_info_by_reference_type_and_cust_name',
        args: {
            customer: row.customer || frm.doc.customer,
            reference_type: row.reference_type,
            search_text: search_text
        },
        callback: function(r) {
            if(r.message && r.message.reference_name) {
                // Filter out already selected reference names
                const options = r.message.reference_name
                    .filter(item => !selected_references.includes(item.name))
                    .map(item => item.name);

                awesomplete.list = options;

                // Show dropdown immediately if field is focused
                if(document.activeElement === awesomplete.input) {
                    awesomplete.evaluate();
                }
            }
        }
    });
}

