        args: {
            all: dialog.get_value('all') ? 1 : 0,
            executive: dialog.get_value('executive') || '',
            deposit_date: dialog.get_value('date') || '',
            payment_mode: dialog.get_value('payment_mode') || '',
            customer: dialog.get_value('customer') || '',
