                if (amounts && amounts.length) {
                    dialog.set_df_property('amount', 'options', amounts);
                }
                if (payment_mode && payment_mode.length) {
                    dialog.set_df_property('payment_mode', 'options', payment_mode);
                }
                if (customer && customer.length) {
                    dialog.set_df_property('customer', 'options', customer);
                }
            } else if (r.message && r.message.error) {
                frappe.msgprint(__('Error: ' + r.message.error));
            } else {
                frappe.msgprint(__('No data available for filtering.'));
            }
        },
