function open_remark_dialog(actionName, frm, dialog) {
    let remarkDialog = null;

    // Get the selected receipt number
    const selectedReceipt = $('.receipt-table1:checked').closest('tr').find('.receipt-number').text();

    // Check if a receipt is selected
    if (!selectedReceipt) {
        frappe.msgprint(__('Please select a receipt.'));
        return;
    }

    // Check if the dialog already exists
    if (remarkDialog) {
        remarkDialog.show();
        return;
    }

    remarkDialog = new frappe.ui.Dialog({
        title: `${actionName} - Add Remark`,
        fields: [
            {
                label: 'Remark',
                fieldname: 'remark',
                fieldtype: 'Small Text',
                placeholder: `Enter your remark for ${actionName}`
            }
        ],
        primary_action_label: 'Submit',
        primary_action: function() {
            const remark = remarkDialog.get_value('remark');
            rejectReceiptIntimation(frm, remarkDialog, selectedReceipt, remark);
            remarkDialog.hide();
            remarkDialog = null;  // Clear the dialog reference
        }
    });
    remarkDialog.show();
}
}

