function moveToJournal(frm, dialog, selectedReceipts) {
    if (selectedReceipts && selectedReceipts.length > 0) {
        console.log('function moveToJournal:', selectedReceipts);

        // Return the promise from frappe.call
        return frappe.call({
            method: 'sil.services.payment_info_journal_entry_api.MovePaymentInfoForJournalEntry',
            args: {
                receipt_no: selectedReceipts
            },
            callback: function (r) {
                console.log('Server Response:', r);

                if (r.message && r.message.status === "success") {
                    frappe.msgprint({
                        title: __('Action Completed'),
                        message: `Successfully moved the receipt [${selectedReceipts}] to Journal Entry.`,
                        indicator: 'blue'
                    });
                    console.log(`Receipts moved to journal entry: [${selectedReceipts}]`);

                    // Optionally refresh the dialog or perform other UI updates
                    dialog.refresh();
                } else if (r.message && r.message.status === "error") {
                    frappe.msgprint({
                        title: __('Action Failed'),
                        message: `Failed to move receipt(s) [${selectedReceipts}]: ${r.message.error}`,
                        indicator: 'red'
                    });
                    console.error(`Error while moving receipts: ${r.message.error}`);
                } else {
                    frappe.msgprint({
                        title: __('No Action Taken'),
                        message: __('No data was updated. Please check the receipt numbers.'),
                        indicator: 'orange'
                    });
                    console.warn('No data was updated for the selected receipts.');
                }
            },
            error: function (err) {
                console.error('Error while calling the server method:', err);
                frappe.msgprint({
                    title: __('Server Error'),
                    message: __('An error occurred while processing the request. Please try again later.'),
                    indicator: 'red'
                });
            }
        });
    } else {
        console.warn('No receipts selected by the user.');
        frappe.msgprint({
            title: __('No Receipt Selected'),
            message: __('Please select at least one receipt to move to journal entry.'),
            indicator: 'orange'
        });
    }
}



