function addCheckboxValidation(selector, selectedReceipts) {
    // Remove any existing event listeners to prevent duplicates
    $(document).off('change', selector).on('change', selector, function () {
        const receiptNumber = $(this).val();
        if ($(this).is(':checked')) {
            // Uncheck all other checkboxes in the same group
            $(selector).not(this).prop('checked', false);
            // Clear the array and add only the selected receipt
            selectedReceipts.length = 0;
            selectedReceipts.push(receiptNumber);
        } else {
            // If unchecked, clear the array
            selectedReceipts.length = 0;
        }
        console.log("function addCheckboxValidation:", selectedReceipts);
    });
}

