function filterReceipts() {
    const selectedExecutive = $('#executive_filter').val();
    const selectedAmount = $('#amount_filter').val();

    // Filter only table1 (suspense details) based on dropdown selections
    let filteredTable1 = [...table1_data];
    if (selectedExecutive) {
        filteredTable1 = filteredTable1.filter(item => item.executive === selectedExecutive);
    }
    if (selectedAmount) {
        filteredTable1 = filteredTable1.filter(item => parseFloat(item.amount_1) === parseFloat(selectedAmount));
    }

    // Render table1 with filtered data
    renderTable('.table1-body', filteredTable1, 'table1');
    // Render table2 with all data (unfiltered)
    renderTable('.table2-body', table2_data, 'table2');
}


// Add checkbox change handler for table1
function addTable1CheckboxHandler() {
    $(document).on('change', '.receipt-table1', function() {
        if ($(this).is(':checked')) {
            // Uncheck other checkboxes in table1
            $('.receipt-table1').not(this).prop('checked', false);

            // Get the selected row data
            const row = $(this).closest('tr');
            const referenceNumber = row.find('td:eq(3)').text().trim();
            const referenceDate = row.find('td:eq(4)').text().trim();
            const amount = parseFloat($(this).data('amount'));

            let filteredTable2;

            // First attempt: Try filtering by reference number, date, and amount
            if (referenceNumber && referenceNumber !== 'null' && 
                referenceDate && referenceDate !== 'null') {

                filteredTable2 = table2_data.filter(item => 
                    item.reference_number_2 === referenceNumber && 
                    item.reference_date_2 === referenceDate &&
                    parseFloat(item.amount_2) === amount
                );

                console.log('Attempting reference match:', {
                    referenceNumber,
                    referenceDate,
                    amount,
                    matchesFound: filteredTable2.length
                });
            }

            // If no matches found with reference details, fall back to amount-only filtering
            if (!filteredTable2 || filteredTable2.length === 0) {
                console.log('No reference matches found, filtering by amount only');
                filteredTable2 = table2_data.filter(item => 
                    parseFloat(item.amount_2) === amount
                );

                if (filteredTable2.length > 0) {
                    // Show an informative message about falling back to amount-based matching
                    frappe.show_alert({
                        message: __('No exact reference match found. Showing entries with matching amount.'),
                        indicator: 'yellow'
                    }, 5);
                }
            }

            // If still no matches found, show an alert
            if (filteredTable2.length === 0) {
                frappe.msgprint(__('No matching entries found with the same amount.'));
                $(this).prop('checked', false); // Uncheck the checkbox
                renderTable('.table2-body', table2_data, 'table2'); // Show all data
                return;
            }

            // Render filtered table2
            renderTable('.table2-body', filteredTable2, 'table2');
        } else {
            // If unchecked, show all table2 data
            renderTable('.table2-body', table2_data, 'table2');
        }
    });
}

// Helper function to safely check if a value is null/empty
function isNullOrEmpty(value) {
    return value === null || value === undefined || value.toString().trim() === '' || value === 'null';
}

// Add this new function to update dropdown options
function updateDropdownOptions(selectedExecutive, selectedAmount) {
    // Get unique values only from table1_data
    const uniqueExecutives = new Set();
    const uniqueAmounts = new Set();

    // Always use table1_data as the source for dropdown values
    let filteredData = [...table1_data];

    // Apply existing filters when updating dropdowns
    if (selectedExecutive) {
        filteredData = filteredData.filter(item => item.executive === selectedExecutive);
    }
    if (selectedAmount) {
        filteredData = filteredData.filter(item => parseFloat(item.amount_1) === parseFloat(selectedAmount));
    }

    // Populate unique values from filtered data
    filteredData.forEach(item => {
        if (!selectedExecutive) uniqueExecutives.add(item.executive);
        if (!selectedAmount) uniqueAmounts.add(item.amount_1);
    });

    // Update executive dropdown if not currently selected
    if (!selectedExecutive) {
        const executiveOptions = ['<option value="">Select Executive</option>']
            .concat(Array.from(uniqueExecutives)
                .sort()
                .map(executive => `<option value="${executive}">${executive}</option>`));
        $('#executive_filter').html(executiveOptions.join(''));
    }

    // Update amount dropdown if not currently selected
    if (!selectedAmount) {
        const amountOptions = ['<option value="">Select Amount</option>']
            .concat(Array.from(uniqueAmounts)
                .sort((a, b) => parseFloat(a) - parseFloat(b))
                .map(amount => `<option value="${amount}">${amount}</option>`));
        $('#amount_filter').html(amountOptions.join(''));
    }
}

function renderTable(selector, data, tableType) {
    const tableBody = $(selector);

    // Get the parent table
    const table = tableBody.closest('table');

    // If this is the first render, store the initial width as a data attribute
    if (!table.data('initial-width') && table.width() > 0) {
        table.data('initial-width', table.width());
    }

    // Get the stored initial width
    const initialWidth = table.data('initial-width');

    tableBody.empty();

    data.forEach(item => {
        const row = tableType === 'table1'
            ? `
                <tr>
                    <td><input type="checkbox" class="receipt-table1" data-receipt-id="${item.receipt_id_1}" data-amount="${item.amount_1}" /></td>
                    <td>${item.receipt_id_1}</td>
                    <td class="executive-cell">${item.executive}</td>
                    <td>${item.reference_number_1}</td>
                    <td>${item.reference_date_1}</td>
                    <td>${formatDecimal(item.amount_1)}</td>
                </tr>
            `
            : `
                <tr>
                    <td><input type="checkbox" class="receipt-table2" data-receipt-id="${item.receipt_id_2}" data-amount="${item.amount_2}" /></td>
                    <td>${item.receipt_id_2}</td>
                    <td class="executive-cell">${item.executive}</td>
                    <td>${item.reference_number_2}</td>
                    <td>${item.reference_date_2}</td>
                    <td>${formatDecimal(item.amount_2)}</td>
                </tr>
            `;
        tableBody.append(row);
    });

    // Apply fixed width for table2
    if (tableType === 'table2' && initialWidth) {
        // Force the table width to stay constant
        table.css({
            'width': initialWidth,
            'min-width': initialWidth,
            'max-width': initialWidth
        });
    }

    // Add or update CSS to maintain table stability
    if (!document.getElementById('table-stability-styles')) {
        $('<style>')
            .prop('type', 'text/css')
            .attr('id', 'table-stability-styles')
            .html(`
                table {
                    table-layout: fixed !important;
                }
                td {
                    white-space: nowrap;
                    overflow: hidden;
                    text-overflow: ellipsis;
                }
                /* Prevent width changes on hover/selection */
                tr:hover td, tr.selected td {
                    width: auto !important;
                }
                /* Ensure checkbox column doesn't resize */
                td:first-child {
                    width: 30px !important;
                    min-width: 30px !important;
                    max-width: 30px !important;
                }
                /* Prevent selection from affecting layout */
                input[type="checkbox"] {
                    margin: 0;
                    padding: 0;
                }
            `)

        tableBody.append(row);
    }

}

function rejectReceiptIntimation(frm, dialog, selectedReceipt, remark) {
    if (!selectedReceipt || !remark) {
        frappe.msgprint(__('Please select a receipt and provide a remark.'));
        return;
    }

    // Call the server-side function for rejection
    frappe.call({
        method: 'sil.services.payment_info_journal_entry_api.UpdatePaymentInfoForRejection',
        args: {
            receipt_no: selectedReceipt,  // Pass the selected receipt ID
            remark: remark
        },
        callback: function(response) {
            if (response.message.status === "success") {
                frappe.msgprint(response.message.message);
                dialog.hide();  // Close the dialog on success
            } else {
                frappe.msgprint(__('An error occurred while rejecting the receipt.'));
            }
        }
    });
}

function removeFromJournal(receipt_no) {
    // Validate if the receipt number is provided
    if (!receipt_no) {
        frappe.msgprint(__('Please select a valid receipt.'));
        return;
    }

    // Call the server-side method via frappe.call
    frappe.call({
        method: 'sil.services.payment_info_journal_entry_api.RemovePaymentInfoFromJournalEntry',  // Path to the server-side function
        args: {
            receipt_no: receipt_no  // Passing the receipt number to the server function
        },
        callback: function(response) {
            if (response.message.status === 'success') {
                // Show success message
                frappe.msgprint(__('Receipt status updated successfully.'));
            } else {
                // Show error message
                frappe.msgprint(__('Failed to update the receipt status.'));
            }
        },
        error: function(error) {
            // In case of error, show error message
            frappe.msgprint(__('An error occurred while trying to update the receipt.'));
        }
    });
}

