                    let item_details = r.message
                    frm.set_value('custom_item_classification', item_details.custom_item_classification);
                    frm.set_value('custom_item', item_details.item);
                    frm.set_value('custom_item_name', item_details.custom_item_name);
                    frm.set_value('customer', item_details.customer);
                    frm.set_value('custom_city_or_town', item_details.city_or_town);
                    frm.set_value('custom_customer_address', item_details.full_address);


                } else {
                    frappe.msgprint("No Details found for this Serial No");
                }
            }

        });
    }
    else{
        const fields = ["custom_item_classification","custom_item","custom_item_name","customer"]
        for(let field of fields){
            frm.set_value( field, "");
        }
    }
    },
    customer: function(frm) {
        if (frm.doc.customer) {
            frappe.call({
                method: "sil_v2.services.api.get_customer_addresses",
                args: {
                    customer: frm.doc.customer
                },
                callback: function(r) {
                    if (r.message && r.message.length > 0) {
                        let address = r.message[0].full_address;
                        frm.set_value('custom_customer_address', address);
                        frm.set_value('custom_city_or_town', r.message[0].city_or_town);
                          frm.refresh_field('custom_city_or_town');
                        console.log(r.message[0].city_or_town)

                    } else {
                        frappe.msgprint("No address found for this customer.");
                    }
                }
            });
        }
        else{
            frm.set_value('custom_customer_address', "");
            frm.set_value('custom_city_or_town', "");
        }
    },


    custom_service_status_: function (frm) {
        // Sync logic when service status is changed
        if (frm.doc.custom_service_status_ === 'Resolved') {
            frm.set_value('custom_issue_status', 'Resolved');
        }
        else {
            frm.set_value('custom_issue_status', 'Open');
        }

        // Refresh and apply styles
        frm.refresh_field('custom_issue_status');
        style_custom_issue_status(frm);
    }
});

function style_custom_issue_status(frm) {
    setTimeout(() => {
        const field = frm.get_field('custom_issue_status');
        if (field && field.$wrapper) {
            const value = frm.doc.custom_issue_status;

            // Default styling
            let background = '#e8f0fe'; // fallback
            if (value === 'Resolved') {
                background = '#e6f4ea'; // pale green
            }

            field.$wrapper.find('.control-value').css({
                'background-color': background,
                'padding': '4px 8px',
                'border-radius': '20px',
                'width': 'fit-content',
                'display': 'inline-block',
                'color': 'black'
            });
        }
    }, 100);
}

frappe.ui.form.on('Test1', {

    // Form refresh event
    refresh(frm) {
        let taskName = frm.doc.taskname;
        let currStatus = frm.doc.taskstatus;
        frm.set_value("tasknote", `${taskName} is currently ${currStatus}`);
    },

    // Field change event for taskstatus
    taskstatus(frm) {
        let taskName = frm.doc.taskname;
        let currStatus = frm.doc.taskstatus;
        if (currStatus) {
            frappe.show_alert({
                message: __("Task status successfully updated to " + currStatus),
                indicator: 'blue'
            }, 5);

            frm.set_value("tasknote", `${taskName} is currently ${currStatus}`);
        }
    },

    // Field change event for due_date
    due_date(frm) {
        let selectDate = frm.doc.due_date;
        let today = frappe.datetime.get_today();

        if (selectDate && frappe.datetime.get_diff(selectDate, today) <= 0) {
            frappe.msgprint(__("Please select an upcoming date!!!"));

            // Prevent loop/recursion
            if (frm.doc.due_date) {
                frm.set_value("due_date", "");
            }
        }
    }
});
